import React, { Component } from 'react';
import UserService from '../services/UserService';
import { withRouter } from 'react-router-dom'; // Importa withRouter para acceder a las props de enrutamiento

class ViewUserComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            user: {}
        };

        // Bind the method to the class
        this.handleBack = this.handleBack.bind(this);
    }

    componentDidMount() {
        UserService.getUserById(this.state.id).then(res => {
            this.setState({ user: res.data });
        });
    }

    handleBack() {
        // Navega de vuelta a la lista de usuarios
        this.props.history.push('/users'); // Asegúrate de que esta ruta sea la correcta para tu aplicación
    }

    render() {
        return (
            <div>
                <br />
                <div className="card col-md-6 offset-md-3">
                    <h3 className="text-center">Ver detalles del usuario</h3>
                    <div className="card-body">
                        <div className="row">
                            <label><b>Nombres: </b></label>
                            <div>{this.state.user.firstName}</div>
                        </div>
                        <div className="row">
                            <label><b>Apellidos: </b></label>
                            <div>{this.state.user.lastName}</div>
                        </div>
                        <div className="row">
                            <label><b>Correo electrónico: </b></label>
                            <div>{this.state.user.email}</div>
                        </div>
                        {/* Botón de retorno */}
                        <button onClick={this.handleBack} className="btn btn-primary">Volver a la lista</button>
                    </div>
                </div>
            </div>
        );
    }
}

// Exporta el componente envuelto con withRouter para poder acceder a las props de enrutamiento
export default withRouter(ViewUserComponent);
